#'
#' @export
jmvEstimateCorrelation <- function(
    switch = "fromraw",
    data,
    dv,
    iv,
    show.line = FALSE,
    show.meanCI = FALSE,
    show.PI = FALSE,
    predictx = "none",
    r=0,
    n=0,
    conf.level = 95,
    ylab,
    xlab,
    size = 3,
    ymin = "auto",
    ymax = "auto",
    xmin = "auto",
    xmax = "auto") {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvEstimateCorrelation requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dv)) dv <- jmvcore::resolveQuo(jmvcore::enquo(dv))
    if ( ! missing(iv)) iv <- jmvcore::resolveQuo(jmvcore::enquo(iv))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dv), dv, NULL),
            `if`( ! missing(iv), iv, NULL))


    options <- jmvEstimateCorrelationOptions$new(
        switch = switch,
        dv = dv,
        iv = iv,
        show.line = show.line,
        show.meanCI = show.meanCI,
        show.PI = show.PI,
        predictx = predictx,
        r = r,
        n = n,
        conf.level = conf.level,
        ylab = ylab,
        xlab = xlab,
        size = size,
        ymin = ymin,
        ymax = ymax,
        xmin = xmin,
        xmax = xmax)

    analysis <- jmvEstimateCorrelationClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
