#'
#' @export
jmvEstimateCorrelationDifference <- function(
    switch = "fromraw",
    data,
    dv,
    iv,
    group,
    r1=0,
    n1=1,
    r2=0,
    n2=1,
    grouplabel1 = "Group 1",
    grouplabel2 = "Group 2",
    varlabel1 = "Variable 1",
    varlabel2 = "Variable 2",
    conf.level = 95) {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvEstimateCorrelationDifference requires jmvcore to be installed (restart may be required)')

    if ( ! missing(dv)) dv <- jmvcore::resolveQuo(jmvcore::enquo(dv))
    if ( ! missing(iv)) iv <- jmvcore::resolveQuo(jmvcore::enquo(iv))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dv), dv, NULL),
            `if`( ! missing(iv), iv, NULL),
            `if`( ! missing(group), group, NULL))


    options <- jmvEstimateCorrelationDifferenceOptions$new(
        switch = switch,
        dv = dv,
        iv = iv,
        group = group,
        r1 = r1,
        n1 = n1,
        r2 = r2,
        n2 = n2,
        grouplabel1 = grouplabel1,
        grouplabel2 = grouplabel2,
        varlabel1 = varlabel1,
        varlabel2 = varlabel2,
        conf.level = conf.level)

    analysis <- jmvEstimateCorrelationDifferenceClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
