jmvEstimatePairedMeanDifference <- function(
    switch = "fromraw",
    data,
    measure1,
    measure2,
    reference.group = FALSE,
    m1=0,
    s1=0,
    m2=0,
    s2=0,
    n1=0,
    r=0,
