    g1color = "white",
    alpha = 0.3,
    bsize = 2,
    bcex = 1,
    ropeBottom = "none",
    ropeTop = "none") {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvEstimatePairedMeanDifference requires jmvcore to be installed (restart may be required)')

    if ( ! missing(measure1)) measure1 <- jmvcore::resolveQuo(jmvcore::enquo(measure1))
    if ( ! missing(measure2)) measure2 <- jmvcore::resolveQuo(jmvcore::enquo(measure2))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(measure1), measure1, NULL),
            `if`( ! missing(measure2), measure2, NULL))


    options <- jmvEstimatePairedMeanDifferenceOptions$new(
        switch = switch,
        measure1 = measure1,
        measure2 = measure2,
        reference.group = reference.group,
        m1 = m1,
        s1 = s1,
        m2 = m2,
        s2 = s2,
        n1 = n1,
        r = r,
        conf.level = conf.level,
        var.equal = var.equal,
        ymin = ymin,
        ymax = ymax,
        ylab = ylab,
        xlab = xlab,
        g1lab = g1lab,
        g2lab = g2lab,
        g1color = g1color,
        alpha = alpha,
        bsize = bsize,
        bcex = bcex,
        ropeBottom = ropeBottom,
        ropeTop = ropeTop)

    analysis <- jmvEstimatePairedMeanDifferenceClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
