jmvEstimateProportion <- function(
    switch = "fromraw",
    data,
    measure1,
    case.level,
    na.rm = "dontremove",
    cases = 1,
    n = 1,
    caselabel1 = "Affected",
    caselabel2 = "Not affected",
    conf.level = 95,
    ylab = "auto",
    xlab = "auto",
    ropeBottom = "none",
    ropeTop = "none") {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvEstimateProportion requires jmvcore to be installed (restart may be required)')

    if ( ! missing(measure1)) measure1 <- jmvcore::resolveQuo(jmvcore::enquo(measure1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(measure1), measure1, NULL))


    options <- jmvEstimateProportionOptions$new(
        switch = switch,
        measure1 = measure1,
        case.level = case.level,
        na.rm = na.rm,
        cases = cases,
        n = n,
        caselabel1 = caselabel1,
        caselabel2 = caselabel2,
        conf.level = conf.level,
        ylab = ylab,
        xlab = xlab,
        ropeBottom = ropeBottom,
        ropeTop = ropeTop)

    analysis <- jmvEstimateProportionClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
