jmvEstimateProportionDifference <- function(
    switch = "fromraw",
    data,
    measure1,
    measure2,
    case.level = NULL,
    category.level = NULL,
    cases1 = 1,
    n1 = 1,
    cases2 = 1,
    n2 =1,
    caselabel1 = "Affected",
    caselabel2 = "Not affected",
    grouplabel1 = "Group 1",
    grouplabel2 = "Group 2",
    conf.level = 95,
    ylab = "auto",
    xlab = "auto",
    ropeBottom = "none",
    ropeTop = "none") {

    if ( ! requireNamespace('jmvcore'))
        stop('jmvEstimateProportionDifference requires jmvcore to be installed (restart may be required)')

    if ( ! missing(measure1)) measure1 <- jmvcore::resolveQuo(jmvcore::enquo(measure1))
    if ( ! missing(measure2)) measure2 <- jmvcore::resolveQuo(jmvcore::enquo(measure2))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(measure1), measure1, NULL),
            `if`( ! missing(measure2), measure2, NULL))


    options <- jmvEstimateProportionDifferenceOptions$new(
        switch = switch,
        measure1 = measure1,
        measure2 = measure2,
        case.level = case.level,
        category.level = category.level,
        cases1 = cases1,
        n1 = n1,
        cases2 = cases2,
        n2 = n2,
        caselabel1 = caselabel1,
        caselabel2 = caselabel2,
        grouplabel1 = grouplabel1,
        grouplabel2 = grouplabel2,
        conf.level = conf.level,
        ylab = ylab,
        xlab = xlab,
        ropeBottom = ropeBottom,
        ropeTop = ropeTop)

    analysis <- jmvEstimateProportionDifferenceClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
